// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.15;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "./RewardsPoolV1.sol";

/**
 * @title OwnersRewardsPool
 * @dev Handles distribution of pool owners rewards
 */
contract OwnersRewardsPoolV1 is RewardsPoolV1 {
    using SafeERC20 for IERC677;

    address public poolOwners;
    uint256 public distributedRewards;

    event RewardDistributed(address indexed sender, uint256 amountStaked, uint256 amount);

    constructor(
        address _poolOwners,
        address _rewardsToken,
        string memory _dTokenName,
        string memory _dTokenSymbol
    ) RewardsPoolV1(_poolOwners, _rewardsToken, _dTokenName, _dTokenSymbol) {
        poolOwners = _poolOwners;
    }

    /**
     * @dev withdraws a user's earned rewards
     * @param _amount amount to withdraw
     **/
    function withdraw(uint256 _amount) public virtual override {
        uint256 toWithdraw = _amount;

        if (_amount == type(uint256).max) {
            toWithdraw = balanceOf(msg.sender);
        }

        distributedRewards -= toWithdraw;
        super.withdraw(toWithdraw);
    }

    /**
     * @dev withdraws all of a user's earned rewards
     * @param _account user to withdraw for
     **/
    function withdraw(address _account) external virtual nonReentrant {
        require(msg.sender == poolOwners, "PoolOwners only");

        uint256 toWithdraw = balanceOf(_account);

        if (toWithdraw > 0) {
            _updateReward(_account);
            _burn(_account, toWithdraw);
            distributedRewards -= toWithdraw;
            rewardsToken.safeTransfer(_account, toWithdraw);
            emit Withdrawn(_account, toWithdraw);
        }
    }

    /**
     * @dev ERC677 implementation that automatically calls distributeRewards
     * @param _sender of the token transfer
     * @param _value of the token transfer
     **/
    function onTokenTransfer(
        address _sender,
        uint256 _value,
        bytes calldata
    ) external nonReentrant {
        require(msg.sender == address(rewardsToken), "Sender must be rewards token");
        distributeRewards();
    }

    /**
     * @dev distributes new rewards that have been deposited
     **/
    function distributeRewards() public {
        require(stakingDerivative.totalSupply() > 0, "Cannot distribute when nothing is staked");
        uint256 toDistribute = rewardsToken.balanceOf(address(this)) - distributedRewards;
        distributedRewards += toDistribute;
        _updateRewardPerToken(toDistribute);
        emit RewardDistributed(msg.sender, stakingDerivative.totalSupply(), toDistribute);
    }
}

contracts/core/base/StakingRewardsPool.s.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.15;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "../tokens/base/VirtualERC677.sol";

/**
 * @title StakingRewardsPool
 * @notice Handles staking and reward distribution for a single asset
 * @dev Rewards can be positive or negative (user balances can increase and decrease)
 */
abstract contract StakingRewardsPool is VirtualERC677 {
    using SafeERC20 for IERC677;

    IERC677 public immutable token;

    mapping(address => uint) private shares;
    uint public totalShares;

    constructor(
        address _token,
        string memory _derivativeTokenName,
        string memory _derivativeTokenSymbol
    ) VirtualERC677(_derivativeTokenName, _derivativeTokenSymbol) {
        token = IERC677(_token);
    }

    /**
     * @notice returns the total supply of staking derivative tokens
     * @return total supply
     */
    function totalSupply() public view override(IERC20, VirtualERC20) returns (uint) {
        return _totalStaked();
    }

    /**
     * @notice returns an account's stake balance
     * @param _account account to return balance for
     * @return account's stake balance
     **/
    function balanceOf(address _account) public view override(IERC20, VirtualERC20) returns (uint) {
        uint balance = getStakeByShares(shares[_account]);
        if (balance < 100) {
            return 0;
        } else {
            return balance;
        }
    }

    /**
     * @notice returns an account's share balance
     * @param _account account to return balance for
     * @return account's share balance
     **/
    function sharesOf(address _account) public view returns (uint) {
        return shares[_account];
    }

    /**
     * @notice returns the amount of shares that corresponds to a staked amount
     * @param _amount staked amount
     * @return amount of shares
     **/
    function getSharesByStake(uint256 _amount) public view returns (uint256) {
        uint totalStaked = _totalStaked();
        if (totalStaked == 0) {
            return 0;
        } else {
            return (_amount * totalShares) / totalStaked;
        }
    }

    /**
     * @notice returns the amount of stake that corresponds to an amount of shares
     * @param _amount shares amount
     * @return amount of stake
     **/
    function getStakeByShares(uint256 _amount) public view returns (uint256) {
        if (totalShares == 0) {
            return 0;
        } else {
            return (_amount * _totalStaked()) / totalShares;
        }
    }

    /**
     * @notice returns the total amount of assets staked in the pool
     * @return total staked amount
     */
    function _totalStaked() internal view virtual returns (uint);

    /**
     * @notice transfers a stake balance from one account to another
     * @param _sender account to transfer from
     * @param _recipient account to transfer to
     * @param _amount amount to transfer
     **/
    function _transfer(
        address _sender,
        address _recipient,
        uint _amount
    ) internal override {
        uint sharesToTransfer = getSharesByStake(_amount);

        require(_sender != address(0), "Transfer from the zero address");
        require(_recipient != address(0), "Transfer to the zero address");
        require(shares[_sender] >= sharesToTransfer, "Transfer amount exceeds balance");

        shares[_sender] -= sharesToTransfer;
        shares[_recipient] += sharesToTransfer;

        emit Transfer(_sender, _recipient, _amount);
    }

    /**
     * @notice mints new shares to an account
     * @dev takes a stake amount and calculates the amount of shares it corresponds to
     * @param _recipient account to mint shares for
     * @param _amount stake amount
     **/
    function _mint(address _recipient, uint _amount) internal override {
        uint sharesToMint = getSharesByStake(_amount);
        if (sharesToMint == 0) {
            sharesToMint = _amount;
        }

        _mintShares(_recipient, sharesToMint);
    }

    /**
     * @notice mints new shares to an account
     * @param _recipient account to mint shares for
     * @param _amount shares amount
     **/
    function _mintShares(address _recipient, uint _amount) internal {
        require(_recipient != address(0), "Mint to the zero address");

        totalShares += _amount;
        shares[_recipient] += _amount;
    }

    /**
     * @notice burns shares belonging to an account
     * @dev takes a stake amount and calculates the amount of shares it corresponds to
     * @param _account account to burn shares for
     * @param _amount stake amount
     **/
    function _burn(address _account, uint _amount) internal override {
        uint sharesToBurn = getSharesByStake(_amount);

        require(_account != address(0), "Burn from the zero address");
        require(shares[_account] >= sharesToBurn, "Burn amount exceeds balance");

        totalShares -= sharesToBurn;
        shares[_account] -= sharesToBurn;
    }
}

contracts/core/StakingPool.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.15;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

import "./base/StakingRewardsPool.sol";
import "./interfaces/IStrategy.sol";
import "./interfaces/IWrappedSDToken.sol";
import "./interfaces/IDelegatorPool.sol";

/**
 * @title Staking Pool
 * @notice Allows users to stake an asset and receive derivative tokens 1:1, then deposits staked
 * assets into strategy contracts to earn returns
 */
contract StakingPool is StakingRewardsPool, Ownable {
    using SafeERC20 for IERC677;

    struct Fee {
        address receiver;
        uint basisPoints;
    }

    address[] private strategies;
    uint public totalStaked;
    uint public liquidityBuffer;

    Fee[] private fees;

    address public immutable poolRouter;
    address public immutable delegatorPool;
    uint16 public poolIndex;

    event Stake(address indexed account, uint amount);
    event Withdraw(address indexed account, uint amount);
    event UpdateStrategyRewards(address indexed account, uint totalStaked, int rewardsAmount, uint totalFees);

    constructor(
        address _token,
        string memory _derivativeTokenName,
        string memory _derivativeTokenSymbol,
        Fee[] memory _fees,
        address _poolRouter,
        address _delegatorPool
    ) StakingRewardsPool(_token, _derivativeTokenName, _derivativeTokenSymbol) {
        for (uint i = 0; i < _fees.length; i++) {
            fees.push(_fees[i]);
        }
        poolRouter = _poolRouter;
        delegatorPool = _delegatorPool;
    }

    modifier onlyRouter() {
        require(poolRouter == msg.sender, "PoolRouter only");
        _;
    }

    /**
     * @notice returns a list of all active strategies
     * @return list of strategies
     */
    function getStrategies() external view returns (address[] memory) {
        return strategies;
    }

    /**
     * @notice returns a list of all fees
     * @return list of fees
     */
    function getFees() external view returns (Fee[] memory) {
        return fees;
    }

    /**
     * @notice stakes asset tokens and mints derivative tokens
     * @param _account account to stake for
     * @param _amount amount to stake
     **/
    function stake(address _account, uint _amount) external onlyRouter {
        require(strategies.length > 0, "Must be > 0 strategies to stake");

        token.safeTransferFrom(msg.sender, address(this), _amount);
        depositLiquidity();

        _mint(_account, _amount);
        totalStaked += _amount;

        emit Stake(_account, _amount);
    }

    /**
     * @notice withdraws asset tokens and burns derivative tokens
     * @dev will withdraw from strategies if not enough liquidity
     * @param _account account to withdraw for
     * @param _receiver address to receive withdrawal
     * @param _amount amount to withdraw
     **/
    function withdraw(
        address _account,
        address _receiver,
        uint _amount
    ) external onlyRouter {
        uint toWithdraw = _amount;
        if (_amount == type(uint).max) {
            toWithdraw = balanceOf(_account);
        }

        uint balance = token.balanceOf(address(this));
        if (toWithdraw > balance) {
            _withdrawLiquidity(toWithdraw - balance);
        }
        require(token.balanceOf(address(this)) >= toWithdraw, "Not enough liquidity available to withdraw");

        _burn(_account, toWithdraw);
        totalStaked -= toWithdraw;
        token.safeTransfer(_receiver, toWithdraw);

        emit Withdraw(_account, toWithdraw);
    }

    /**
     * @notice deposits assets in a strategy
     * @param _index index of strategy to deposit in
     * @param _amount amount to deposit
     **/
    function strategyDeposit(uint _index, uint _amount) external onlyOwner {
        require(_index < strategies.length, "Strategy does not exist");
        IStrategy(strategies[_index]).deposit(_amount);
    }

    /**
     * @notice withdraws assets from a strategy
     * @param _index index of strategy to withdraw from
     * @param _amount amount to withdraw
     **/
    function strategyWithdraw(uint _index, uint _amount) external onlyOwner {
        require(_index < strategies.length, "Strategy does not exist");
        IStrategy(strategies[_index]).withdraw(_amount);
    }

    /**
     * @notice returns the maximum amount that can be staked via the pool
     * @return the overall staking limit
     **/
    function getMaxDeposits() public view returns (uint256) {
        uint256 max;

        for (uint i = 0; i < strategies.length; i++) {
            IStrategy strategy = IStrategy(strategies[i]);
            max += strategy.getMaxDeposits();
        }
        if (liquidityBuffer > 0) {
            max += (max * liquidityBuffer) / 10000;
        }
        return max;
    }

    /**
     * @notice returns the minimum amount that must remain the pool
     * @return min deposit
     */
    function getMinDeposits() public view returns (uint256) {
        uint256 min;

        for (uint i = 0; i < strategies.length; i++) {
            IStrategy strategy = IStrategy(strategies[i]);
            min += strategy.getMinDeposits();
        }

        return min;
    }

    /**
     * @notice returns the available deposit room for this pool
     * @return available deposit room
     */
    function canDeposit() external view returns (uint256) {
        uint max = getMaxDeposits();

        if (max <= totalStaked) {
            return 0;
        } else {
            return max - totalStaked;
        }
    }

    /**
     * @notice returns the available withdrawal room for this pool
     * @return available withdrawal room
     */
    function canWithdraw() external view returns (uint256) {
        uint min = getMinDeposits();

        if (min >= totalStaked) {
            return 0;
        } else {
            return totalStaked - min;
        }
    }

    /**
     * @notice adds a new strategy
     * @param _strategy address of strategy to add
     **/
    function addStrategy(address _strategy) external onlyOwner {
        require(!_strategyExists(_strategy), "Strategy already exists");
        token.safeApprove(_strategy, type(uint).max);
        strategies.push(_strategy);
    }

    /**
     * @notice removes a strategy
     * @param _index index of strategy to remove
     **/
    function removeStrategy(uint _index) external onlyOwner {
        require(_index < strategies.length, "Strategy does not exist");

        uint[] memory idxs = new uint[](1);
        idxs[0] = _index;
        updateStrategyRewards(idxs);

        IStrategy strategy = IStrategy(strategies[_index]);
        uint totalStrategyDeposits = strategy.getTotalDeposits();
        if (totalStrategyDeposits > 0) {
            require(strategy.canWithdraw() == totalStrategyDeposits, "Strategy contains deposits that cannot be withdrawn");
            strategy.withdraw(totalStrategyDeposits);
        }

        for (uint i = _index; i < strategies.length - 1; i++) {
            strategies[i] = strategies[i + 1];
        }
        strategies.pop();
        token.safeApprove(address(strategy), 0);
    }

    /**
     * @notice reorders strategies
     * @param _newOrder array containing strategy indexes in a new order
     **/
    function reorderStrategies(uint[] calldata _newOrder) external onlyOwner {
        require(_newOrder.length == strategies.length, "newOrder.length must = strategies.length");

        address[] memory strategyAddresses = new address[](strategies.length);
        for (uint i = 0; i < strategies.length; i++) {
            strategyAddresses[i] = strategies[i];
        }

        for (uint i = 0; i < strategies.length; i++) {
            require(strategyAddresses[_newOrder[i]] != address(0), "all indices must be valid");
            strategies[i] = strategyAddresses[_newOrder[i]];
            strategyAddresses[_newOrder[i]] = address(0);
        }
    }

    /**
     * @notice adds a new fee
     * @param _receiver receiver of fee
     * @param _feeBasisPoints fee in basis points
     **/
    function addFee(address _receiver, uint _feeBasisPoints) external onlyOwner {
        fees.push(Fee(_receiver, _feeBasisPoints));
    }

    /**
     * @notice updates an existing fee
     * @param _index index of fee
     * @param _receiver receiver of fee
     * @param _feeBasisPoints fee in basis points
     **/
    function updateFee(
        uint _index,
        address _receiver,
        uint _feeBasisPoints
    ) external onlyOwner {
        require(_index < fees.length, "Fee does not exist");

        if (_feeBasisPoints == 0) {
            fees[_index] = fees[fees.length - 1];
            fees.pop();
        } else {
            fees[_index].receiver = _receiver;
            fees[_index].basisPoints = _feeBasisPoints;
        }
    }

    /**
     * @notice Sets the liquidity buffer. The liquidity buffer will increase the max staking limit
     * of the pool by always keeping a % of the staked token as liquid within the pool. The buffer
     * has the effect of diluting yield, but promotes pool liquidity with any lock-in that would prevent
     * the un-wind of allowance.
     * @param _liquidityBufferBasisPoints basis points to use for the liquidity buffer
     **/
    function setLiquidityBuffer(uint _liquidityBufferBasisPoints) external onlyOwner {
        liquidityBuffer = _liquidityBufferBasisPoints;
    }

    /**
     * @notice updates and distributes rewards based on balance changes in strategies
     * @param _strategyIdxs indexes of strategies to update rewards for
     **/
    function updateStrategyRewards(uint[] memory _strategyIdxs) public {
        int totalRewards;
        uint totalFeeAmounts;
        uint totalFeeCount;
        address[][] memory receivers = new address[][](strategies.length + 1);
        uint[][] memory feeAmounts = new uint[][](strategies.length + 1);

        for (uint i = 0; i < _strategyIdxs.length; i++) {
            IStrategy strategy = IStrategy(strategies[_strategyIdxs[i]]);
            int rewards = strategy.depositChange();
            if (rewards != 0) {
                (address[] memory strategyReceivers, uint[] memory strategyFeeAmounts) = strategy.updateDeposits();
                totalRewards += rewards;
                if (rewards > 0) {
                    receivers[i] = (strategyReceivers);
                    feeAmounts[i] = (strategyFeeAmounts);
                    totalFeeCount += receivers[i].length;
                    for (uint j = 0; j < strategyReceivers.length; j++) {
                        totalFeeAmounts += strategyFeeAmounts[j];
                    }
                }
            }
        }

        if (totalRewards != 0) {
            totalStaked = uint(int(totalStaked) + totalRewards);
        }

        if (totalRewards > 0) {
            uint currentRate = IDelegatorPool(delegatorPool).currentRate(address(token), poolIndex);
            uint feesLength = currentRate > 0 ? fees.length + 1 : fees.length;

            receivers[receivers.length - 1] = new address[](feesLength);
            feeAmounts[feeAmounts.length - 1] = new uint[](feesLength);
            totalFeeCount += feesLength;

            for (uint i = 0; i < fees.length; i++) {
                receivers[receivers.length - 1][i] = fees[i].receiver;
                feeAmounts[feeAmounts.length - 1][i] = (uint(totalRewards) * fees[i].basisPoints) / 10000;
                totalFeeAmounts += feeAmounts[feeAmounts.length - 1][i];
            }

            if (currentRate > 0) {
                receivers[receivers.length - 1][fees.length] = delegatorPool;
                feeAmounts[feeAmounts.length - 1][fees.length] = (uint(totalRewards) * currentRate) / 10000;
                totalFeeAmounts += feeAmounts[feeAmounts.length - 1][fees.length];
            }
        }

        if (totalFeeAmounts > 0) {
            uint sharesToMint = (totalFeeAmounts * totalShares) / (totalStaked - totalFeeAmounts);
            _mintShares(address(this), sharesToMint);

            uint feesPaidCount;
            for (uint i = 0; i < receivers.length; i++) {
                for (uint j = 0; j < receivers[i].length; j++) {
                    if (feesPaidCount == totalFeeCount - 1) {
                        transferAndCallFrom(address(this), receivers[i][j], balanceOf(address(this)), "0x00");
                    } else {
                        transferAndCallFrom(address(this), receivers[i][j], feeAmounts[i][j], "0x00");
                        feesPaidCount++;
                    }
                }
            }
        }

        emit UpdateStrategyRewards(msg.sender, totalStaked, totalRewards, totalFeeAmounts);
    }

    /**
     * @notice deposits available liquidity into strategies by order of priority
     * @dev deposits into strategies[0] until its limit is reached, then strategies[1], and so on
     **/
    function depositLiquidity() public {
        uint toDeposit = token.balanceOf(address(this));
        if (toDeposit > 0) {
            for (uint i = 0; i < strategies.length; i++) {
                IStrategy strategy = IStrategy(strategies[i]);
                uint canDeposit = strategy.canDeposit();
                if (canDeposit >= toDeposit) {
                    strategy.deposit(toDeposit);
                    break;
                } else if (canDeposit > 0) {
                    strategy.deposit(canDeposit);
                    toDeposit -= canDeposit;
                }
            }
        }
    }

    /**
     * @notice sets the index of this pool as stored in the pool router
     * @param _poolIndex index of pool
     */
    function setPoolIndex(uint16 _poolIndex) external onlyRouter {
        poolIndex = _poolIndex;
    }

    /**
     * @notice returns the total amount of assets staked in the pool
     * @return the total staked amount
     */
    function _totalStaked() internal view override returns (uint) {
        return totalStaked;
    }

    /**
     * @notice withdraws liquidity from strategies in opposite order of priority
     * @dev withdraws from strategies[strategies.length - 1], then strategies[strategies.length - 2], and so on
     * until withdraw amount is reached
     * @param _amount amount to withdraw
     **/
    function _withdrawLiquidity(uint _amount) private {
        uint toWithdraw = _amount;

        for (uint i = strategies.length; i > 0; i--) {
            IStrategy strategy = IStrategy(strategies[i - 1]);
            uint canWithdraw = strategy.canWithdraw();

            if (canWithdraw >= toWithdraw) {
                strategy.withdraw(toWithdraw);
                break;
            } else if (canWithdraw > 0) {
                strategy.withdraw(canWithdraw);
                toWithdraw -= canWithdraw;
            }
        }
    }

    /**
     * @notice checks whether or not a strategy exists
     * @param _strategy address of strategy
     * @return true if strategy exists, false otherwise
     **/
    function _strategyExists(address _strategy) private view returns (bool) {
        for (uint i = 0; i < strategies.length; i++) {
            if (strategies[i] == _strategy) {
                return true;
            }
        }
        return false;
    }
}

